import styled from 'styled-components';
import React from "react";

const BackgroundWrapper = styled.div`
    background-image: linear-gradient(to right, #8E2DE2 , #4A00E0);
    box-shadow: 0px 2px #FFF053;
`;
const UlWrapper = styled.ul`
  list-style-type: none;
  margin: 0;
  padding: 0;
  overflow: hidden;
`;
const ListItem = styled.li`
  float: ${props => props.left ? 'left': 'right'};
  padding-right: 4px;
   a {
      display: block;
      color: white;
      text-align: center;
      text-decoration: none;
  }
  a:hover {
  background-color: #111;
}
`;
const ImageWrapper = styled.div`
    padding:8px 8px;
    height:24px;
    width: auto;
    background: url(${props => props.imgUrl});
    background-repeat: no-repeat;
    background-size: contain;
    background-position: center;
    -o-transition:.5s;
    -ms-transition:.5s;
    -moz-transition:.5s;
    -webkit-transition:.5s;
    transition: .5s;
    &:hover {
        // background-color: ${props => props.bgColor};
        background-color: #fff;
        background: url(${props => props.hoverImgUrl});
        background-repeat: no-repeat;
        background-size: contain;
        background-position: center;
    }
`;

const social_list = [
    {
        title: 'linked-in',
        imgUrl: 'https://img.icons8.com/ios-glyphs/128/ffffff/linkedin.png',
        link: '',
        bgColor: '#0073a4',
        hoverImgUrl: 'https://img.icons8.com/ios-glyphs/128/0073a4/linkedin.png',
    },
    {
        title: 'stackoverflow',
        imgUrl: 'https://img.icons8.com/ios/128/ffffff/stackoverflow-filled.png',
        link: '',
        bgColor: '#f48024',
        hoverImgUrl: 'https://img.icons8.com/ios/128/f48024/stackoverflow-filled.png',
    },
    {
        title: 'twitter',
        imgUrl: 'https://img.icons8.com/ios-glyphs/128/ffffff/twitter-circled.png',
        link: '',
        bgColor: '#00aced',
        hoverImgUrl: 'https://img.icons8.com/ios-glyphs/128/00aced/twitter-circled.png',
    },
    {
        title: 'github',
        imgUrl: 'https://img.icons8.com/ios-glyphs/128/ffffff/github.png',
        link: '',
        bgColor: '#24292d',
        hoverImgUrl: 'https://img.icons8.com/ios-glyphs/128/24292d/github.png',
    },
];

const NavBar = () => (
    <BackgroundWrapper>
        <UlWrapper>
            {social_list.map(item=> (
                <ListItem>
                    <a href={item.link}>
                        <ImageWrapper imgUrl={item.imgUrl} bgColor={item.bgColor} hoverImgUrl={item.hoverImgUrl}/>
                    </a>
                </ListItem>
            ))}
        </UlWrapper>
    </BackgroundWrapper>
);

export default NavBar;
