import React from "react";
import Layout from '../../components/Layout';
import TaskList from '../../components/taskList';
import { HalfContainer } from '../../components/wrappers';
import styled from 'styled-components';
import { ColDiff, Row } from "../../components/webcomps";

import SkillSet from './skillset';
import Whoami from './whoami';
import ContactMe from './contactme';
import { projectsList, historyList, articlesList } from './dataConstants';

const TitleWrapper = styled.p`
    font-size: 36px;
    opacity: 0.5;
    font-family: 'Monoton', cursive;
    text-align: right;
    margin-right: 8px;
    @media (max-width: 767px) {
        margin-right: 0px;
        text-align: center;
    }
`;

const ImageWrapper = styled.img`
  opacity: 0.5;
  border-radius: 16px;
  width: 128px;
  margin: auto;
  box-shadow: 0 19px 38px #8E2DE2, 0 15px 12px #FFF053;
`;

const Index = () => (
    <Layout>
      <Row>
        <ColDiff flexbasis="70%">
            {/*<TitleWrapper>NEVER SETTLE</TitleWrapper>*/}
            <Whoami/>
            <TaskList listHeader="ls projects/" taskList={projectsList} />
            <SkillSet/>
            <TaskList listHeader="history" taskList={historyList} />
            <TaskList listHeader="ls articles/" taskList={articlesList} />
            <ContactMe />
        </ColDiff>
        <ColDiff>
          <ImageWrapper src='/static/images/coding_prop.png'/>
        </ColDiff>
      </Row>
      <TitleWrapper>NEVER SETTLE</TitleWrapper>
    </Layout>
);
export default Index;
