import styled from 'styled-components';
import React from "react";

const BackgroundWrapper = styled.div`
    background-image: linear-gradient(to right, #8E2DE2 , #4A00E0);
    box-shadow: 0px 2px #FFF053;
`;
const UlWrapper = styled.ul`
  list-style-type: none;
  margin: 0;
  padding: 0;
  overflow: hidden;
`;
const ListItem = styled.li`
  float: ${props => props.left ? 'left': 'right'};
  padding-right: 4px;
   a {
      display: block;
      color: white;
      text-align: center;
      text-decoration: none;
  }
  a:hover {
  background-color: #111;
}
`;
const ImageWrapper = styled.img`
    padding:8px 12px;
    height:32px;
    width: auto;
    -o-transition:.5s;
    -ms-transition:.5s;
    -moz-transition:.5s;
    -webkit-transition:.5s;
    transition: .5s;
    &:hover {
        background-color: #24292d;
        // background-color: ${props => props.bgColor};
    }
`;

const social_list = [
    {
        title: 'linked-in',
        imgUrl: 'https://img.icons8.com/ios/128/000000/linkedin-filled.png',
        link: '',
        bgColor: '#0073a4',
    },
    {
        title: 'stackoverflow',
        imgUrl: '/static/linkedin-logo.png',
        link: '',
        bgColor: '#f48024',
    },
    {
        title: 'twitter',
        imgUrl: 'https://img.icons8.com/ios/128/000000/twitter-circled-filled.png',
        link: '',
        bgColor: '#00aced',
    },
    {
        title: 'github',
        imgUrl: '/static/github.png',
        link: '',
        bgColor: '#24292d',
    },
];

const NavBar = () => (
    <BackgroundWrapper>
        <UlWrapper>
            {social_list.map(item=> (
                <ListItem>
                    <a href={item.link}><ImageWrapper src={item.imgUrl} bgColor={item.bgColor}/></a>
                </ListItem>
            ))}
        </UlWrapper>
    </BackgroundWrapper>
);

export default NavBar;
