import styled from 'styled-components';
import SectionTitle from "../../components/sectionTitle";
import Container from '../../components/wrappers';

const LiWrapper = styled.li`
    color: #54C9FF;
    margin-bottom: .7em;
    list-style: none;
    &:before {
        content: '~';
        margin-right: 1rem;
    };
    span {
        color: #fff;
    }
`;

const projectsList = [
    'Nuddles Node.js wrapper for the Foursquare API',
    'Jabbbar Python 3 wrapper for the Dribbble API',
    'Wintermute A RESTful API for browsing Cyberpunk Authors/Novels/Characters',
    'Neuromancer.vim A vim colorscheme for the console jockeys',
    'Night-City The Ansible playbook I made to provision my development boxes',
    'F-91W My photography site. Written in React.js',
    'GoCatchEmA Restful API for Browsing Pokemon Stats. Written in Go.',
];

const Projects = () => (
    <Container>
        <SectionTitle title="ls projects/"/>
        <ul>
            {projectsList.map(item => (
                <LiWrapper>{item}</LiWrapper>
            ))}
        </ul>
    </Container>
);

export default Projects;
