import styled from 'styled-components';
import Container from '../components/wrappers';
import SectionTitle from './sectionTitle';

const InfoSection = styled.div`
    font-size: 16px;
`;
const Whoami = () => (
    <Container>
        <SectionTitle title="whoami" />
        <div>
            <p>
                My name is Abhishek Kasana, I'm a 25 years old full stack developer based in Paris, France (currently)
                and Brighton, UK. In Paris, I connect people together at Numa, and when I'm in Brighton I mentor
                under-represented students at Codebar.
            </p>
            <p>I'm a massive cyberpunk nerd with an interest in open source and devops. In my free time I like to hack on fun projects, play / watch football with friends, gulp banana smoothies and take pictures (among other things, like obsessing over my Hacker News karma).</p>
        </div>
    </Container>
);

export default Whoami;
