import React from 'react';
import styled from 'styled-components';
import Container from '../components/wrappers';

const Row = styled.div`
    box-sizing: border-box;
    display: flex;
    flex: 0 1 auto;
    flex-direction: row;
    flex-wrap: wrap;
`;
const Col = styled.div`
    box-sizing: border-box;
    display: flex;
    flex: 0 1 auto;
    flex-basis: 50%;
    flex-direction: column;
`;

const LiWrapper = styled.li`
    color: #54C9FF;
    margin-bottom: .7em;
    list-style: none;
    &:before {
        content: '~';
        margin-right: 1rem;
    };
    span {
        color: #fff;
    }
`;
const skillsetList = [
    {
        title: 'Languages',
        value: [
            'Golang',
            'Python',
            'Node.js',
            'Javascript (Front-End) / ES6-7',
            'HTML5',
            'CSS3',
            'Sass',
            'SQL',
            'Markdown',
        ],
    },
    {
        title: 'Tools',
        value: [
            'Vim / VS Code',
            'Tmux',
            'Fish Shell',
            'Virtualbox',
            'Vagrant',
            'Docker (Currently having fun with)',
            'Ansible',
            'Gulp',
        ],
    },
    {
        title: 'Frameworks',
        value: [
            'Vim / VS Code',
            'Tmux',
            'Fish Shell',
            'Virtualbox',
            'Vagrant',
            'Docker (Currently having fun with)',
            'Ansible',
            'Gulp',
        ],
    },
    {
        title: 'Other',
        value: [
            'Vim / VS Code',
            'Tmux',
            'Fish Shell',
            'Virtualbox',
            'Vagrant',
            'Docker (Currently having fun with)',
            'Ansible',
            'Gulp',
        ],
    },
];

const Skillset = () => (
    <Container>
        <Row>
            {skillsetList.map(item => (
                <Col xs={6}>
                    <p>{item.title}</p>
                    {item.value.map(subitem => (
                        <LiWrapper><span>{subitem}</span></LiWrapper>
                    ))}
                </Col>
            ))}
        </Row>
    </Container>
);

export default Skillset;
