import styled from 'styled-components';

const Container = styled.div`
    height: inherit;
    margin: 0 auto;
    padding: 0 16px;
`;

const CompressContainer = styled(Container)`
    @media (min-width: 768px) {
        width: 80%;
        margin: 16px;
    }
`;
const SectionContainer = styled.div`
  margin-bottom: 1em;
  padding: 8px;
`;
export {
    Container,
    CompressContainer,
    SectionContainer,
};
