import styled from 'styled-components';
import SectionTitle from '../../components/sectionTitle';
import { Row, ColDiff, LiWrapper, Text } from '../../components/webcomps';

const CategoryTitle = styled.h3`
    font-size: 1.8em;
    margin: 0 0 0.5em;
    color: #54C9FF;
`;

const skillsetList = [
    {
        title: 'Languages',
        value: [
            'Python',
            'Javascript',
            'Java',
            'Node.js',
            'HTML5',
            'CSS3',
            'C++',
            'SQL',
        ],
    },
    {
        title: 'Tools/OS',
        value: [
            'Vim',
            'Bash',
            'Docker (Currently having fun with)',
            'Ubuntu(Linux)',
            'Kali',
            'Windows'
        ],
    },
    {
        title: 'Frameworks',
        value: [
            'Django',
            'NEXT JS',
            'Android',
            'Flask',
            'React.js',
        ],
    },
    // {
    //     title: 'Other',
    //     value: [
    //         'HTTP / REST / OAuth2 / API Development',
    //         'Flexbox',
    //         'Git',
    //         'Celery',
    //         'Basic UNIX Admin',
    //         'OOP',
    //         'Nginx',
    //         'AWS (EC2, S3)',
    //     ],
    // },
];

const Skillset = () => (
    <div>
        <SectionTitle title="cat skills.md" />
        <Row>
            {skillsetList.map(item => (
                <ColDiff key={item.title}>
                    <CategoryTitle>{item.title}</CategoryTitle>
                    {item.value.map(subitem => (
                        <LiWrapper key={subitem}><Text>{subitem}</Text></LiWrapper>
                    ))}
                </ColDiff>
            ))}
        </Row>
    </div>
);

export default Skillset;
