import styled from 'styled-components';
import Container from '../../components/wrappers';
import SectionTitle from '../../components/sectionTitle';

const Row = styled.div`
    box-sizing: border-box;
    display: flex;
    flex: 0 1 auto;
    flex-direction: row;
    flex-wrap: wrap;
`;
const Col = styled.div`
    box-sizing: border-box;
    display: flex;
    flex: 0 1 auto;
    flex-basis: 50%;
    flex-direction: column;
`;

const CategoryTitle = styled.h3`
    font-size: 1.4em;
    margin-bottom: 1em;
    color: #54C9FF;
`;
const LiWrapper = styled.li`
    color: #54C9FF;
    margin-bottom: .7em;
    list-style: none;
    &:before {
        content: '~';
        margin-right: 1rem;
    };
    span {
        color: #fff;
    }
`;
const skillsetList = [
    {
        title: 'Languages',
        value: [
            'Golang',
            'Python',
            'Node.js',
            'Javascript (Front-End) / ES6-7',
            'HTML5',
            'CSS3',
            'Sass',
            'SQL',
            'Markdown',
        ],
    },
    {
        title: 'Tools',
        value: [
            'Vim / VS Code',
            'Tmux',
            'Fish Shell',
            'Virtualbox',
            'Vagrant',
            'Docker (Currently having fun with)',
            'Ansible',
            'Gulp',
        ],
    },
    {
        title: 'Frameworks',
        value: [
            'Express JS',
            'Django',
            'Flask',
            'Angular 4',
            'React.js',
            'Mocha / Chai',
            'Bourbon / Neat',
        ],
    },
    {
        title: 'Other',
        value: [
            'HTTP / REST / OAuth2 / API Development',
            'CSS Animations / Flexbox / BEM',
            'Git / Unit Testing / CI',
            'Celery / RabbitMQ',
            'Basic UNIX Admin',
            'OOP',
            'Nginx (SSL, Static asset caching)',
            'AWS (EC2, S3)',
            'Online Payments with Stripe',
        ],
    },
];

const Skillset = () => (
    <Container>
        <SectionTitle title="cat skills.md" />
        <Row>
            {skillsetList.map(item => (
                <Col key={item.title}>
                    <CategoryTitle>{item.title}</CategoryTitle>
                    {item.value.map(subitem => (
                        <LiWrapper><span>{subitem}</span></LiWrapper>
                    ))}
                </Col>
            ))}
        </Row>
    </Container>
);

export default Skillset;
