import styled from "styled-components";

const Row = styled.div`
    box-sizing: border-box;
    display: flex;
    flex: 0 1 auto;
    flex-direction: row;
    flex-wrap: wrap;
`;

const ColDiff = styled.div`
    box-sizing: border-box;
    display: flex;
    flex: 0 1 auto;
    flex-basis: 50%;
    flex-direction: column;
`;

const LiWrapper = styled.li`
    color: #54C9FF;
    margin-bottom: .7em;
    list-style: none;
    &:before {
        content: '~';
        margin-right: 1rem;
    };
    span {
        color: #fff;
    }
`;


export {
    Row,
    ColDiff,
    LiWrapper,
}
